function Dialog(){}Dialog.redrawOnResize=function(a){Dialog.redraw(a);$(window).on("resize.dialogResize",function(){Dialog.redraw(a)})};Dialog.redraw=function(a){var b=$("#"+a);var c=b.find(".dialog-box");if(b.is(":visible")){if(c.data("fixed")){c.centerFixed()}else{c.center()}}else{b.css({visibility:"hidden",display:"block"});if(c.data("fixed")){c.centerFixed()}else{c.center()}b.css({visibility:"",display:"none"});$("#"+a).fadeIn()}};$(document).ready(function(){$(document).on("click",".dialog-container .dialog-background, .dialog-container .dialog-box .dialog-close, .dialog-container .dialog-box .close",function(){$(this).closest(".dialog-container").fadeOut();$(window).off("resize.dialogResize")})});